// Given a string s, find the longest palindromic substring in s. You may assume that the maximum length of s is 1000.

// Example:

// Input: "babad"

// Output: "bab"

// Note: "aba" is also a valid answer.
// Example:

// Input: "cbbd"

// Output: "bb"

/**
 * @param {string} s
 * @return {string}
 */

function longestPalindrome(s) {

    if (s.length <= 1) {
        return s;
    }

    // explore solution space
    let si = sj = 0;
    let ci = cj = 0;
    for (let i = 0; i < s.length; i++) {
        [ci, cj] = longestPalindromeFrom(s, i, i);
        if ((cj - ci) > (sj - si)) {
            [si, sj] = [ci, cj];
        }
        [ci, cj] = longestPalindromeFrom(s, i, i + 1);
        if ((cj - ci) > (sj - si)) {
            [si, sj] = [ci, cj];
        }
    }

    return s.substring(si, sj);
}

function longestPalindromeFrom(s, left, right) {
    let i = left;
    let j = right;
    while (s[i] === s[j] && i >= 0 && j < s.length) {
        i--;
        j++;
    }
    return [i + 1, j];
}

const tests = [
    'abcde',
    'a',
    'ccc',
    'badad',
    'cbbd',
    '',
    '',
    '',
    '',
    '',
    '',
    '',
];

tests.forEach(test => {
    const started = Date.now();
    const solution = longestPalindrome(test);
    const took = Date.now() - started;
    console.log(`[${took}ms] ${solution}`);
})


